                $($(this)[0].files).each(function () {
                    var file = $(this);
                    if (regex.test(file[0].name.toLowerCase())) {
                        var reader = new FileReader();
                        reader.onload = function (e) {
                            var img = $(""<img />"");
                            img.attr(""style"", ""height:100px;width: 100px"");
                            img.attr(""src"", e.target.result);
                            dvPreview.append(img);
